<?php include_once("web/BHM.php");accessLog("replot $dir$plot");if(!file_exists("web/BHMprotect.php")){echo "This feature is not available.";return;}include_once("web/BHMprotect.php");?><?php $parts=preg_split("/\./",$plot);$fname=$parts[0];$file="$ROOTDIR/$dir/$plot";$image="$dir/$fname.png";$stdout="plot-stdout-$SESSID";$stderr="plot-stderr-$SESSID";$file_stdout="$wTMPDIR/plot-stdout-$SESSID";$file_stderr="$wTMPDIR/plot-stderr-$SESSID";echo "<html><head><link rel='stylesheet' type='text/css' href='$RELATIVE/web/BHM.css'></head><body>";if(isset($action)){$fs=fopen($file,"w");fwrite($fs,$script);fclose($fs);$rdir=preg_replace("/\/\//","",$dir);$cmd="$PYTHONCMD $ROOTDIR/$rdir/$plot 2> $file_stderr |tee $file_stdout";shell_exec($cmd);}$script=shell_exec("cat $file");$script=preg_replace("/^\s*/","",$script);echo "<form method='post'> <input type='hidden' name='dir' value='$dir'><input type='hidden' name='plot' value='$plot'><div class='wrapper'> <div class='formarea' style='width:60%'><h3>Script</h3><b>Name:</b>$plot<br/><!--<b>Command:</b>$cmd<br/>--><b>Output:</b><a href='$file_stdout' target=_blank>Output</a>|<a href='$file_stderr' target=_blank>Error</a><br/><br/><textarea name='script' style='width:90%;height:100%'>$script</textarea><br/><div style='position:absolute;top:10%;right:55%'><input type='hidden' name='action' value='replot'><button>Replot</button></div></div><div class='results' style='width:40%'><img src='$image' width='100%'></div></div></form></body></html>";?>
